import * as actionTypes from "./actions";

const initialState = {
  persons: []
};

const personsReducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.ADD_PERSON:
      return state;

    case actionTypes.DELETE_PERSON:
      return state;

    default:
      return state;
  }
};

export default personsReducer;
